Getting group-level adjustments

brm_5_shrunk &lt;- brm_5 |&gt;
  spread_draws(r_Subject[Subject,term]) |&gt;
  mean_qi() |&gt;
  mutate(source = "shrunk")

gmean_RT &lt;- mean(mald$RT_log, na.rm = TRUE)

original &lt;- mald |&gt;
  group_by(Subject) |&gt;
  summarise(
    mean_RT = mean(RT_log) ,
    sd_RT = sd(RT_log),
    lower = mean_RT - 1.96 * sd_RT,
    upper = mean_RT + 1.96 * sd_RT
  )
